package app.Servlets.Filters;import javax.servlet.*;import javax.servlet.http.Cookie;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;public class IsAuthFilter implements Filter {    public void init(FilterConfig filterConfig) throws ServletException {        System.out.println("filter init");    }    public void doFilter(ServletRequest servletRequest, ServletResponse servletResponse, FilterChain filterChain) throws IOException, ServletException {        servletRequest.setCharacterEncoding("UTF-8");        HttpServletRequest request = (HttpServletRequest)servletRequest;        HttpServletResponse response = (HttpServletResponse)servletResponse;        String mailCookie=null;        for(Cookie cookie: request.getCookies()){            if(cookie.getName().equals("currentUser"))                mailCookie = cookie.getValue();        }        if(mailCookie == null || mailCookie.equals("")){            response.sendRedirect("auth");        }else{            servletRequest.setAttribute("userMail", mailCookie);            filterChain.doFilter(servletRequest,servletResponse);        }    }    public void destroy() {        System.out.println("filter destroy");    }}